import './style.css';
import Header from '../../components/Header'
import Controls from '../../components/Controls'
import Card from '../../components/Card'
import { musics } from '../../musics'
import { useRef, useState } from 'react';


function Home() {
  const audioRef = useRef(null);
  const [iconBtn, setIconBtn] = useState('pause');

  const [musicsData, setMusicsData] = useState([...musics])
  const [currentMusic, setCurrentMusic] = useState({
    id: 0, title: '', artist: ''
  });

  function setMusic(music) {
    setIconBtn('play');
    audioRef.current.src = music.url;
    setCurrentMusic(music);
  }

  function handleChangeMusic(option) {
    const newMusicId = option === 'next'
      ? currentMusic.id + 1
      : currentMusic.id - 1;
  }


  return (
    <div className="container">
      <Header />
      <main>
        <h2>The best play list</h2>
        <div className='container-cards'>
          {musicsData.map((music) => (
            <div
              onClick={() => setMusic(music)}
              key={music.id}
            >
              <Card

                title={music.title}
                cover={music.cover}
                description={music.description}
              />
            </div>
          ))}
        </div>
      </main>
      <Controls
        audioRef={audioRef}
        currentMusic={currentMusic}
        iconBtn={iconBtn}
        setIconBtn={setIconBtn}
        handleChangeMusic={handleChangeMusic}
      />

      <audio ref={audioRef}>

      </audio>
    </div>
  );
}

export default Home;
